import React, { useEffect, useState } from 'react';
import { getTransactions } from '../api/api';
import './TransactionDashboard.css';

const TransactionList = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchText, setSearchText] = useState('');
    const [month, setMonth] = useState('March'); // Default month

    // Fetch transactions when the component mounts or the month or search text changes
    useEffect(() => {
        const fetchTransactions = async () => {
            setLoading(true);
            try {
                const data = await getTransactions(month, searchText); // Send searchText to the API
                setTransactions(data.transactions);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTransactions();
    }, [month, searchText]);

    // Handler for search input change
    const handleSearchChange = (e) => {
        setSearchText(e.target.value);
    };

    // Handler for month dropdown change
    const handleMonthChange = (e) => {
        setMonth(e.target.value);
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="transaction-dashboard">
            <h2>Transaction Dashboard</h2>

            <div className="controls">
                <input
                    type="text"
                    placeholder="Search transaction"
                    value={searchText}
                    onChange={handleSearchChange}
                />
                <select value={month} onChange={handleMonthChange}>
                    <option value="January">January</option>
                    <option value="February">February</option>
                    <option value="March">March</option>
                    {/* Add other months */}
                </select>
            </div>

            <table className="transactions-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Category</th>
                        <th>Sold</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.length === 0 ? (
                        <tr>
                            <td colSpan="7">No transactions found</td>
                        </tr>
                    ) : (
                        transactions.map(transaction => (
                            <tr key={transaction.id}>
                                <td>{transaction.id}</td>
                                <td>{transaction.title}</td>
                                <td>{transaction.description}</td>
                                <td>${transaction.price}</td>
                                <td>{transaction.category}</td>
                                <td>{transaction.sold ? 'Yes' : 'No'}</td>
                                <td>
                                    <img src={transaction.imageUrl} alt={transaction.title} width="50" />
                                </td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default TransactionList;
