// src/api/api.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8000'; // Adjust this to match your API's base URL

// Function to get transactions based on filters
export const getTransactions = async (month, title, page, perPage) => {
    try {
        const response = await axios.get(`http://localhost:8000/transaction?month=January`, {
            params: {
                month,
                title,
                page,
                perPage
            }
        });
        return response.data;
    } catch (error) {
        console.error("Error fetching transactions:", error);
        throw error;
    }
};

