// src/api/api.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8000'; // Adjust this to match your API's base URL

// Function to get transactions based on filters
export const getTransactions = async (month, searchText) => {
    try {
        const response = await axios.get(`http://localhost:8000/transaction?month=${month}&page=${searchText}`, {
            params: { month, searchText }
        });
        return response.data;
    } catch (error) {
        console.error("Error fetching transactionsss:", error);
        throw error;
    }
};


// Function to get statistics based on the month
export const getStatistics = async (month) => {
    try {
        const response = await axios.get(`${BASE_URL}/statistics`, {
            params: { month }
        });
        return response.data;
    } catch (error) {
        console.error("Error fetching statistics:", error);
        throw error;
    }
};
