import React, { useState, useEffect } from 'react';
import { getTransactions } from '../api/api'; // Assuming API call function is here
import './TransactionDashboard.css';

const months = [
  "January", "February", "March", "April", "May", "June", 
  "July", "August", "September", "October", "November", "December"
];

const TransactionDashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [month, setMonth] = useState('March'); // Default to March
  const [searchText, setSearchText] = useState('');
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10); // Default 10 items per page

  // Function to fetch transactions
  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const data = await getTransactions(month, searchText, page, perPage);
      setTransactions(data.transactions);
    } catch (err) {
      setError('Error fetching transactions');
    } finally {
      setLoading(false);
    }
  };

  // Call fetchTransactions only when search button is clicked or month is changed
  const handleSearch = () => {
    setPage(1); // Reset to page 1 on search
    fetchTransactions(); // Fetch transactions when search button is clicked
  };

  const handleMonthChange = (e) => {
    setMonth(e.target.value);
    setPage(1); // Reset to page 1 on month change
    fetchTransactions(); // Fetch transactions immediately when month changes
  };

  const handleSearchChange = (e) => {
    setSearchText(e.target.value); // Update the search text state
  };

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
    fetchTransactions(); // Fetch transactions on page change
  };

  const handlePrevPage = () => {
    if (page > 1) {
      setPage((prevPage) => prevPage - 1);
      fetchTransactions(); // Fetch transactions on page change
    }
  };

  // Only fetch transactions when the component mounts or when page changes
  useEffect(() => {
    fetchTransactions(); // Fetch transactions on component mount
  }, [page, perPage]); // Only depend on page and perPage

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="transaction-dashboard">
      <h2>Transaction Dashboard</h2>
      <div className="controls">
        <input
          type="text"
          placeholder="Search transaction by title, price, or description"
          value={searchText}
          onChange={handleSearchChange}
        />
        <button onClick={handleSearch}>Search</button>
        <select value={month} onChange={handleMonthChange}>
          {months.map((m, index) => (
            <option key={index} value={m}>{m}</option>
          ))}
        </select>
      </div>
      <table className="transactions-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Price</th>
            <th>Category</th>
            <th>Sold</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.id}</td>
              <td>{transaction.title}</td>
              <td>{transaction.description}</td>
              <td>${transaction.price}</td>
              <td>{transaction.category}</td>
              <td>{transaction.sold ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button onClick={handlePrevPage} disabled={page === 1}>
          Previous
        </button>
        <span>Page No: {page}</span>
        <button onClick={handleNextPage}>Next</button>
        <span>Per Page: {perPage}</span>
      </div>
    </div>
  );
};

export default TransactionDashboard;
