// src/api/api.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8000'; // Adjust this to match your API's base URL

// Function to get transactions based on filters
export const getTransactions = async (month, searchText, page, perPage) => {
    try {
        const response = await axios.get(`${BASE_URL}/transaction`, {
            params: {
                month,
                title: searchText, // Send the search text as title (this could be any field)
                description:
                page,
                perPage
            }
        });
        return response.data; // Return the fetched data
    } catch (error) {
        console.error("Error fetching transactions:", error);
        throw error;
    }
};


// Function to get statistics based on the month
export const getStatistics = async (month) => {
    try {
        const response = await axios.get(`${BASE_URL}/statistics`, {
            params: { month }
        });
        return response.data;
    } catch (error) {
        console.error("Error fetching statistics:", error);
        throw error;
    }
};
